//@module shortjsdoc
//@class AbstractView @extend Backbone.View @module shortjsdoc

var AbstractView = Backbone.View.extend({

	tagName: 'div'

,	initialize: function(application) 
	{
		this.application = application;
	}

	//@method renderIn renders this view in given parent element @param {jQuery} $parent
,	renderIn: function($parent)
	{
		var template = this.application.templates[this.template]; 
		if(template)
		{			
			var html = template.apply(this, []); 
			this.$el.html(html);
			$parent.append(this.$el); 
		}
		this.afterRender();
		return this;
	}

	//@method render implemented to comply with Backbone View contract
,	render: function()
	{
		return this.renderIn(jQuery(this.el)); 
	}


,	afterRender: function()
	{
		this.renderSource(); 
	}

,	setTitle: function(t)
	{
		// var self = this;
		this.title = t;
		setTimeout(function()
		{
			document.title = t; 
		}, 200);
	}
});



//@module shortjsdoc
// this is the main class for all the application
// utilities for rendering jsdoc data generated by jsdocgenerator1 js-indentator plugin
// using backbone. The user should provide with underscore templates. 
// @class AbstractView @extend Backbone.View @module shortjsdoc
// @author: sgurin

//@class Application 
/*
@constructor Application 

@param {Object} data is the data.json object outputed by running 

	var maker = new JsDocMaker();
	maker.parseFile(myCodeString);
	maker.postProccess();
	var data = maker.data; 
*/
var Application = function(data)
{
	//@property {String} textFormat  can be markdown or html. default: markdown
	this.textFormat = 'markdown'; 

	//@property {Object} templates The templates dictionary (given by template-output.js)
	this.templates = shortjsdoc; 

	/*@property {Object} data is the data.json object outputed by running:

		var maker = new JsDocMaker();
		maker.parseFile(myCodeString);
		maker.postProccess();
		var data = maker.data; 

	*/
	this.data = data; 



	//@property {JsDocMaker} maker the JsDocMaker instance we will use for loading the parsed data and doing some post processing stage (type binding and so)
	this.maker = new JsDocMaker();
	this.maker.data = data;


	this.maker.postProccessBinding();
	this.maker.postProccessInherited(); // <-- important - explicitly ask the framework to calculate inherited methods&properties


	window.shortjsdocdata = data;//for debugging only

	if(jQuery('#mainContainer').size()===0)
	{
		jQuery('body').append('<div id="mainContainer"></div>'); 
	}
	this.$containerEl = jQuery('#mainContainer'); 
};

_(Application.prototype).extend({

	//@method start starts the application by instantiating routers and history and navigating to the index.
	start: function()
	{		
		this.router = new JsDocRouter(this);
		Backbone.history.start();
		var navigateTo = Backbone.history.getHash() || 'index'; 
		Backbone.history.navigate(navigateTo, {trigger: true});
	}

	//@method showView @param {AbstractView} view
,	showView: function(view)
	{
		this.applicationView = this.applicationView || new ApplicationView(this); 
		this.currentView = view;
		this.$containerEl.empty();
		this.applicationView.renderIn(this.$containerEl);
	}

	//@method refreshWithNewModel @param {Object}data
,	refreshWithNewModel: function(data)
	{
		this.data = data; 
		Backbone.history.navigate('#index', {trigger: true}); 
		this.showView(this.currentView);
	}

	//@method showErrorView @param {String}s 
,	showErrorView: function(s) 
	{
		this.$containerEl.empty().append('<h1>'+s+'</h1>'); 
	}

});

//@method start an application loading it with given data. @static
//@param data the output of passing jsindentator JsDocMaker. 
Application.startApplication = function(data)
{
	var app = new Application(data); 
	app.start();
}; 


//@module shortjsdoc
//@class ApplicationView @extends AbstractView
var ApplicationView = AbstractView.extend({

	className: 'application-view'

,	template: 'application'

,	initialize: function(application) 
	{
		this.application = application;
	}

,	renderIn: function($el)
	{
		AbstractView.prototype.renderIn.apply(this, arguments); 

		this.headerView = this.headerView || new HeaderView(this.application);
		this.headerView.renderIn(this.$('[data-type="header-container"]')); 

		this.mainView = this.application.currentView; 

		this.mainView.renderIn(this.$('[data-type="main-view-container"]')); 

		document.title = this.title || window.location.href + '';
	}

});

//@module shortjsdoc
//@class ClassView @extends AbstractView

var ClassView = AbstractView.extend({

	className: 'class-view'

,	template: 'class'

,	initialize: function(application, className, options) 
	{
		this.application = application;
		this.jsdoc = this.application.data.classes[className]; 
		if(!this.jsdoc)
		{
			this.resourceNotFound = true;
			return;
		}

		this.jsdoc.textHtml = this.getTextHtml(this.jsdoc);	

		this.options = options || {};
		this.options.inherited = this.options.inherited ? parseInt(this.options.inherited, 10) : 0;

		this.methods = this.jsdoc.methods;
		if(this.options.inherited)
		{
			this.methods = _(_(this.methods).clone()).extend(this.jsdoc.inherited.methods); 
		}

		this.properties = this.jsdoc.properties;
		if(this.options.inherited)
		{
			this.properties = _(_(this.properties).clone()).extend(this.jsdoc.inherited.properties); 
		}
		//TODO: I'm sorry for the following code:
		var self = this; setTimeout(function(){document.title = self.jsdoc.name + 'Class ';}, 200);

	}

/*
