//@module shortjsdoc
//@class JsDocRouter @extends BackboneRouter
var JsDocRouter = Backbone.Router.extend({

	routes: {
		'class/:class': 'showClass'
	,	'class/:class?:options': 'showClass'

	,	'module/:module': 'showModule'

	,	'method/:method': 'showMethod'
	,	'constructor/:method': 'showMethod'

	,	'property/:property': 'showProperty'
	,	'event/:event': 'showEvent'

	,	'index': 'showIndex'
	,	'modules': 'showModules'
	,	'classes': 'showClasses'

	,	'parse': 'showParse'
	}

,	initialize: function(application) 
	{
		this.application=application;
	}

	//@method showView @param {AbstractView}view @param {String} resourceName
,	showView: function(view, resourceName)
	{
		resourceName = resourceName||'Resource'; 
		if(view.resourceNotFound)
		{
			this.application.showErrorView(resourceName+' '+resourceName+' not found!'); 
		}
		else
		{
			this.application.showView(view); 
		}
	}

	//@method showModule  @param {String} moduleName
,	showModule: function(moduleName) 
	{
		var view = new ModuleView(this.application, moduleName);
		this.showView(view); 
	}

	//method parseOptions @return {Object<String,String>}
,	parseOptions: function(options)
	{
		var params = {};
		_(options.split('&')).each(function(p)
		{
			var a = p.split('='); 
			if (a.length >= 2)
			{
				params[a[0]] = a[1]; 
			}
		}); 
		return params;
	}

	//@method showClass  @param {String} className
,	showClass: function(className, options) 
	{
		options = options || '';
		var params = this.parseOptions(options);		
		var view = new ClassView(this.application, className, params);
		this.showView(view); 
	}

	//@method showMethod  @param {String} method
,	showMethod: function(method)
	{
		var view = new MethodView(this.application, method);
		this.showView(view); 
	}

	//@method showProperty  @param {String} property
,	showProperty: function(property)
	{
		var view = new PropertyView(this.application, property);
		this.showView(view); 
	}

	//@method showEvent  @param {String} event
,	showEvent: function(event)
	{
		var view = new PropertyView(this.application, event, true);
		this.showView(view); 
	}

	//@method showModules
,	showModules: function()
	{
		var view = new AbstractView(this.application);
		view.template = 'modules';
		this.showView(view);
	}

	//@method showClasses
,	showClasses: function()
	{
		var view = new AbstractView(this.application);
		view.template = 'classes';
		this.showView(view);
	}

	//@method showIndex
,	showIndex: function() 
	{
		var view = new IndexView(this.application);
		this.application.showView(view); 
	}

	//@method showParse
,	showParse: function() 
	{
		var view = new ParseView(this.application);
		this.application.showView(view); 
	}

});


//@module shortjsdoc
//@class MethodView @extends AbstractView
var MethodView = AbstractView.extend({

	className: 'method-view'

,	template: 'method'

,	initialize: function(application, methodName) 
	{
		this.application = application;

		var className = this.getClassName(methodName);
		// var propertySimpleName = a[2];
		var methodSimpleName = this.getSimpleName(methodName);

		// var a = methodName.split('.');
		// var className = a[0] + '.' + a[1]; 
		var class_ = this.application.data.classes[className];
		// var methodSimpleName = a[2];
		if(!isNaN(parseInt(methodSimpleName, 10)))
		{
			this.jsdoc = class_.constructors[parseInt(methodSimpleName, 10)]; 
			this.isConstructor = true;
		}
		else
		{			
			this.jsdoc = class_.methods[methodSimpleName]; 
			this.isConstructor = false;
		}
		if(!this.jsdoc)
		{
			this.resourceNotFound = true;
			return;
		}

		this.jsdoc.textHtml = this.getTextHtml(this.jsdoc);
		this.ownerClass = this.application.data.classes[this.jsdoc.ownerClass]; 

	}

});



//@module shortjsdoc
//@class ModuleView @extends AbstractView
var ModuleView = AbstractView.extend({

	className: 'module'

,	template: 'module'

,	initialize: function(application, moduleName) 
	{
		this.application = application;
		this.jsdoc = this.application.data.modules[moduleName]; 
		this.classes = _( _(this.application.data.classes).values() ).filter(function(c){
			return c.module.name === moduleName; 
		}); 

		this.jsdoc.textHtml = this.getTextHtml(this.jsdoc);	
	}

});

